#!/bin/sh -p launchctl setenv XDG_CONFIG_HOME "${XDG_CONFIG_HOME:=$HOME/Library/Preferences}" & launchctl setenv XDG_RUNTIME_DIR "${XDG_RUNTIME_DIR:=${TMPDIR:-$HOME/.temp}}" & launchctl setenv XDG_CACHE_HOME "${XDG_CACHE_HOME:=$HOME/Library/Caches}" & # `getconf DARWIN_USER_CACHE_DIR` launchctl setenv XDG_DATA_HOME "${XDG_DATA_HOME:=$HOME/Library/Application Support}" &
launchctl setenv XDG_STATE_HOME "${XDG_STATE_HOME:=$HOME/Library/Application Support}" &
launchctl setenv XDG_CONFIG_DIRS "/Library/Preferences:/Network/Library/Preferences:/etc" & launchctl setenv XDG_DATA_DIRS "/Library/Application Support:/usr/local/share:/Network/Library/Application Support:/usr/share" &
